#include<iostream>#include<string>using namespace std;  string solve(string &str){        for(int i=0;i<str.size();i++)      {            char ch=str[i];             int j=i+1;            int counter=1;           for(;j<str.size();j++)            {                   if(ch!=str[j])                  {                        break;                  }                  counter++;                             }             if(counter>=7)return "YES";            i=j-1;       }      return "NO";}    int main(){           string str;      cin>>str;       cout<<solve(str)<<endl;      return 0;     }