import java.util.*;public class Program996A{      public static void main(String args[])      {            Scanner sc=new Scanner(System.in);            int number=sc.nextInt();            int ans=0;            while(number>0)            {                  while(number/100>0)                  {                        ans+=number/100;                        number=number%100;                  }                  while(number/20>0)                  {                        ans+=number/20;                        number=number%20;                  }                  while(number/10>0)                  {                        ans+=number/10;                           number=number%10;                  }                  while(number/5>0)                  {                        ans+=number/5;                        number=number%5;                  }                  while(number/1>0)                  {                        ans+=number/1;                        number=number%1;                  }            }            System.out.println(ans);      }}